import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1,
    };
  }
  async componentDidMount() {
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=72bc5fc774594e35a211c6fd69ac2fdf&page=1}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({
       articles: parsedData.articles,
    });

  }



  handlePreviousCick = async () => {
    console.log("Previous");
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=72bc5fc774594e35a211c6fd69ac2fdf&page=${
      this.state.page - 1
    }&pageSize=20`;
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);

    this.setState({
      page: this.state.page - 1,
      articles: parsedData.articles,
    })
  }

  handleNextCick = async () => {
    if(this.state.page + 1 > Math.ceil(this.state.totalResults/20)){

    }
    else{

      let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=72bc5fc774594e35a211c6fd69ac2fdf&page=${
        this.state.page + 1
      }&pageSize=20`;
      let data = await fetch(url);
      let parsedData = await data.json();
      console.log(parsedData);

      this.setState({
        page: this.state.page + 1,
        articles: parsedData.articles,
      })
    }
  }
  render() {
    return (
      <div className="container my-3">
        <h1 className="text-center">NewsMonkey-Top headlines</h1>
        <div className="row">
          {this.state.articles.map((element) => {
            return (
              <div className="col-md-4" key={element.url}>
                <NewsItem
                  title={element.title}
                  description={ element.description }
                  imgUrl={element.urlToImage}
                  url={element.url}
                />
              </div>
            );
          })}
          <div className="container d-flex justify-content-between">
            <button
              disabled={this.state.page <= 1}
              type="button"
              className="btn btn-dark"
              onClick={this.handlePreviousCick}
            >
              &larr; Previous
            </button>
            <button
            disabled = {this.state.page + 1 > Math.ceil(this.state.totalResults/20)}
              type="button"
              className="btn btn-dark"
              onClick={this.handleNextCick}
            >
              Next &rarr;
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default News;
