import React, { Component } from "react";
import NewsItem from "./NewsItem";
import PropTypes from 'prop-types';

export class News extends Component {
  static defaultProps = {
    country : 'in',
    pageSize : '6',
    category : 'General'

  }
  static propsTpes = {
    country : PropTypes.string,
    pageSize : PropTypes.number,
    category : PropTypes.string,
  }

  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      totalResults:0

    };
    document.title = `${this.props.category}-NewsMonkey`;
  }
  async updateNews(){
    const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=72bc5fc774594e35a211c6fd69ac2fdf&page=${
  this.state.page}&pageSize=${this.props.pageSize}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);

    this.setState({
      articles: parsedData.articles,
      totalResults:parsedData.totalResults,
      loading: false})

  }

  async componentDidMount() {
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=72bc5fc774594e35a211c6fd69ac2fdf&page=1&pageSize=${this.props.pageSize}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({
       articles: parsedData.articles,
    loading: false})

  }



  handlePreviousCick = async () => {
   console.log("Previous");
//    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=72bc5fc774594e35a211c6fd69ac2fdf&page=${
//      this.state.page - 1}&pageSize=${this.props.pageSize}`;
//    let data = await fetch(url);
//    let parsedData = await data.json();
//    console.log(parsedData);
//
//    this.setState({
//      page: this.state.page - 1,
//      articles: parsedData.articles,
//    })
this.setState({page: this.state.page - 1})
this.updateNews();
}

  handleNextCick = async () => {
//    if(this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize)){
//
//    }
//    else{
//
//      let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=72bc5fc774594e35a211c6fd69ac2fdf&page=${
//        this.state.page + 1
//      }&pageSize=${this.props.pageSize}`;
//      let data = await fetch(url);
//      let parsedData = await data.json();
//      console.log(parsedData);
//
//      this.setState({
//        page: this.state.page + 1,
//        articles: parsedData.articles,
//        totalResults:parsedData.totalResults
//      })
//    }
this.setState({page: this.state.page+1})
this.updateNews();
  }
  render() {
    return (
      <div className="container my-3">
        <h1 className="text-center">NewsMonkey-Top headlines from {this.props.category}</h1>
        <InfiniteScroll 
        dataLength={this.state.articles.length}
          next={this.fetchMoreData}
          hasMore={this.state.articles.length !==this.totalresults}
          loader={<h4>Loading...</h4>} 
          >

            <div className="row">
          {this.state.articles.map((element) => {
            return <div className="col-md-4" key={element.url}>
                <NewsItem
                  title={element.title}
                  description={ element.description }
                  imgUrl={element.urlToImage}
                  url={element.url}
                  author={element.author}
                  date={element.publishedAt}  
                  source={element.source.name}/>
              </div>
          })}

          </div>

        </InfiniteScroll>

          <div className="container d-flex justify-content-between">
            <button
              disabled={this.state.page <= 1}
              type="button"
              className="btn btn-dark"
              onClick={this.handlePreviousCick}
            >
              &larr; Previous
            </button>
            <button
            disabled = {this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize)}
              type="button"
              className="btn btn-dark"
              onClick={this.handleNextCick}
            >
              Next &rarr;
            </button>
          </div>
        </div>
    );
  }
}

export default News;
